import React from'react';
import Navigation from './components/navigation/Navigation';
import Logo from './components/logo/Logo';
import ImageLinkForm from './components/imageform/ImageLinkForm';
import Rank from './components/rank/Rank';
import ParticlesBg from 'particles-bg';

///////////////////////////////////////////////////////////////////////////////////////////////////
// In this section, we set the user authentication, user and app ID, model details, and the URL
// of the image we want as an input. Change these strings to run your own example.
//////////////////////////////////////////////////////////////////////////////////////////////////

const returnClarifaiRequestOptions = (imageUrl) => {
  // Your PAT (Personal Access Token) can be found in the portal under Authentification
  const PAT = 'e560d7245bf846e18f84f3f90fb41615';
  // Specify the correct user_id/app_id pairings
  // Since you're making inferences outside your app's scope
  const USER_ID = 'dm3rg0';
  const MODEL_ID = 'face-detection';
  const APP_ID = 'face-detector';
  // Change these to whatever model and image URL you want to use
  const IMAGE_URL = imageUrl;
  // To use image bytes, assign its variable   
  // const IMAGE_BYTES_STRING = '';

  const raw = JSON.stringify({
      "user_app_id": {
          "user_id": USER_ID,
          "app_id": APP_ID
      },
      "inputs": [
          {
              "data": {
                  "image": {
                      "url": IMAGE_URL
                      // "base64": IMAGE_BYTES_STRING
                  }
              }
          }
      ]
  });

  const requestOptions = {
      method: 'POST',
      headers: {
          'Accept': 'application/json',
          'Authorization': 'Key ' + PAT
      },
      body: raw
  };

  return requestOptions;
}
//////////////////////////////////////////////////////////////////////////////////////////////////


class App extends React.Component {

  constructor() {
    super();
    this.state = {
      input: '',
    }
  };

  onInputChange =(event) => {
    console.log(event.target.value);
  }

  onButtonSubmit = () => {
    console.log(this.imageURL);
    this.setState({ imageUrl: this.state.input });
//////////////////////////////////////////////////////////////////////////////////////////////////

fetch("https://api.clarifai.com/v2/models/face-detection/outputs",returnClarifaiRequestOptions(this.state.input))
.then(response => response.json())
.then(result => console.log(result))
.catch(error => console.log('error', error));
//////////////////////////////////////////////////////////////////////////////////////////////////

  }

  render() {
  return (
    <div className="App">
      <Navigation />
        <Logo />
        <Rank />
      <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit} />
      {/* <FaceRecognition /> */}
      <ParticlesBg 
        type="cobweb" 
        bg={true}
        color='#FFFFFF'
      />
    </div>

  );
}
}

export default App;
